    </div>
  </div>
</template>

<script>
  import Multiselect from 'vue-multiselect'

  export default {
    components: {
      Multiselect
    },
    props: ['categorys'],
    computed: {},
    methods: {
      categoryAdd(actionName) {
        this.$emit('addFilter', actionName);
      },
      categoryRemove(actionName) {
        this.$emit('removeFilter', actionName);
      },
      search(e) {
        if (e.type != "keyup") {
          return false;
        }
        this.$emit('search', this.$data.query);
      }
    },
    data() {
      return {
        message: '',
        category: '',
        query: '',
        value: []
      }
    }
  }
</script>
